 return path; &#125;else if(cmd.equals(&quot;cd ..&quot;))&#123; //cd .. File f = new File(path); String parent = f.getParent(); if(parent!=null) &#123; return parent; &#125;else &#123; return path; &#125; &#125;else &#123;//cd D:\AA String[] ss = cmd.split(&quot; &quot;); File f = new File(ss[1]); if(f.exists() &amp;&amp; f.isDirectory()) &#123; return f.getPath(); &#125;else &#123; System.out.println(&quot;未能找到&quot;+f.getName()+&quot;目录&quot;); return path; &#125; &#125; &#125; private static void dirOp(String path) &#123; int dirCount = 0; int fileCount = 0; File f = new File(path); File[] childFiles = f.listFiles(); for(int i=0;i&lt;childFiles.length;i++) &#123; File file = childFiles[i]; if(file.isDirectory()) &#123; dirCount++; &#125; long time = file.lastModified(); Date d = new Date(time); SimpleDateFormat sdf = new SimpleDateFormat(&quot;yyyy.MM.dd hh:mm:ss&quot;); System.out.print(sdf.format(d)+&quot;	&quot;+(file.isFile()?&quot; &quot;:&quot;&lt;DIR&gt;&quot;)); if(file.isFile()) &#123; fileCount++; long len = file.length(); String changLength = changeLength(len); System.out.print(&quot;	 &quot;+changLength); &#125;else &#123; System.out.print(&quot;	&quot;); &#125; System.out.print(&quot;		&quot;+file.getName()); System.out.println(); &#125; System.out.println(&quot;
		文件：&quot;+fileCount+&quot;	目录：&quot;+dirCount+&quot;

